import { Layers, Moon, Palette, Sparkles, Sun } from 'lucide-react';
import React, { memo } from 'react';

import { RenderStyle } from '@/shared/types';

import { Btn, CardBtn, CollapsibleSection, Slider, Toggle } from './components';
import {
  COLOR_GRADES,
  EXPOSURE_PRESETS,
  HOLOGRAM_TYPES,
  PARTICLE_TYPES,
  RENDER_STYLES,
} from './constants';
import {
  BRIGHTNESS,
  CONTRAST,
  EXPOSURE,
  HOLOGRAM,
  LIGHT_INTENSITY,
  MATERIAL,
  SATURATION,
  SKETCH as SKETCH_PARAMS,
  VIGNETTE,
} from './EffectsTab.constants';

import type { SceneConfig } from '@/shared/types';

interface EffectsTabProps {
  activeStyle: (typeof RENDER_STYLES)[number] | undefined;
  config: SceneConfig;
  expandedSections: Record<string, boolean>;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
  toggleSection: (key: string) => void;
}

const ColorGradeSection = memo<{
  config: SceneConfig;
  expandedSections: Record<string, boolean>;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
  toggleSection: (key: string) => void;
}>(({ config, set, expandedSections, toggleSection }) => (
  <CollapsibleSection
    expanded={expandedSections.color}
    icon={<Moon className="w-3.5 h-3.5" />}
    onToggle={() => toggleSection('color')}
    title="鑹插僵婊ら暅"
  >
    <div className="grid grid-cols-3 gap-1.5">
      {COLOR_GRADES.map((c) => (
        <button
          className={`py-2 px-2 text-[11px] rounded-lg border transition-all flex flex-col items-center gap-1 ${
            config.colorGrade === c.grade
              ? 'bg-zinc-800 border-zinc-600 text-white ring-1 ring-zinc-500'
              : 'bg-zinc-800/30 border-zinc-700/50 text-zinc-400 hover:border-zinc-600 hover:text-zinc-300'
          }`}
          key={c.grade}
          onClick={() => set('colorGrade', c.grade)}
        >
          <div className="w-4 h-4 rounded-full" style={{ backgroundColor: c.color }} />
          {c.label}
        </button>
      ))}
    </div>
  </CollapsibleSection>
));

export const EffectsTab: React.FC<EffectsTabProps> = memo(
  ({ config, set, expandedSections, toggleSection, activeStyle }) => (
    <>
      <div className="mb-3 p-3 rounded-xl bg-gradient-to-br from-zinc-800/80 to-zinc-800/40 border border-zinc-700/50">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 rounded-lg bg-emerald-500/20 flex items-center justify-center">
            {typeof activeStyle?.icon === 'string' ? (
              <span className="text-lg">{activeStyle.icon}</span>
            ) : (
              <span className="text-emerald-400">{activeStyle?.icon}</span>
            )}
          </div>
          <div className="flex-1">
            <div className="text-sm font-medium text-white">{activeStyle?.label}椋庢牸</div>
            <div className="text-[10px] text-zinc-500">{activeStyle?.desc}</div>
          </div>
          <div className="flex gap-1">
            {config.enableParticles ? (
              <span className="px-1.5 py-0.5 rounded text-[9px] bg-amber-500/20 text-amber-400">
                绮掑瓙
              </span>
            ) : null}
            {config.enableVignette ? (
              <span className="px-1.5 py-0.5 rounded text-[9px] bg-purple-500/20 text-purple-400">
                鏆楄
              </span>
            ) : null}
          </div>
        </div>
      </div>

      <RenderStyleSection
        config={config}
        expandedSections={expandedSections}
        set={set}
        toggleSection={toggleSection}
      />
      <ColorGradeSection
        config={config}
        expandedSections={expandedSections}
        set={set}
        toggleSection={toggleSection}
      />
      <LightingSection
        config={config}
        expandedSections={expandedSections}
        set={set}
        toggleSection={toggleSection}
      />
      <EffectsToggleSection config={config} set={set} />
      <MaterialSection config={config} set={set} />
    </>
  )
);
const EffectsToggleSection = memo<{
  config: SceneConfig;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
}>(({ config, set }) => (
  <CollapsibleSection icon={<Sparkles className="w-3.5 h-3.5" />} title="鐗规晥寮€鍏砛

space-y-1">
      <Toggle
        checked={config.enableParticles}
        label="绮掑瓙鏁堟灉"
        onChange={(v) => set('enableParticles', v)}
      />
      {config.enableParticles ? (
        <div className="ml-4 grid grid-cols-5 gap-1 mt-1 mb-2">
          {PARTICLE_TYPES.map((p) => (
            <Btn
              active={config.particleType === p.type}
              key={p.type}
              onClick={() => set('particleType', p.type)}
              small
            >
              {p.label}
            </Btn>
          ))}
        </div>
      ) : null}
      <Toggle
        checked={config.enableVignette}
        label="鐢靛奖鏆楄"
        onChange={(v) => set('enableVignette', v)}
      />
      {config.enableVignette ? (
        <div className="ml-4 mt-1 mb-2">
          <Slider
            label="鏆楄寮哄害"
            max={VIGNETTE.STRENGTH_MAX}
            min={VIGNETTE.STRENGTH_MIN}
            onChange={(v) => set('vignetteStrength', v)}
            step={VIGNETTE.STRENGTH_STEP}
            value={config.vignetteStrength}
          />
        </div>
      ) : null}
      <Toggle
        checked={config.enableNakedEye3D}
        label="瑁哥溂3D"
        onChange={(v) => set('enableNakedEye3D', v)}
      />
    </div>
  </CollapsibleSection>
));
const LightingSection = memo<{
  config: SceneConfig;
  expandedSections: Record<string, boolean>;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
  toggleSection: (key: string) => void;
}>(({ config, set, expandedSections, toggleSection }) => (
  <CollapsibleSection
    expanded={expandedSections.lighting}
    icon={<Sun className="w-3.5 h-3.5" />}
    onToggle={() => toggleSection('lighting')}
    title="鍏夊奖璋冭妭"
  >
    <Slider
      label="鏇濆厜"
      max={EXPOSURE.MAX}
      min={EXPOSURE.MIN}
      onChange={(v) => set('exposure', v)}
      presets={EXPOSURE_PRESETS as unknown as number[]}
      showPresets
      step={EXPOSURE.STEP}
      value={config.exposure}
    />
    <Slider
      label="浜害"
      max={BRIGHTNESS.MAX}
      min={BRIGHTNESS.MIN}
      onChange={(v) => set('brightness', v)}
      step={BRIGHTNESS.STEP}
      value={config.brightness}
    />
    <Slider
      label="楗卞拰搴

h-px bg-zinc-800 my-2" />
    <Slider
      label="鍏夌収寮哄害"
      max={LIGHT_INTENSITY.MAX}
      min={LIGHT_INTENSITY.MIN}
      onChange={(v) => set('lightIntensity', v)}
      step={LIGHT_INTENSITY.STEP}
      value={config.lightIntensity}
    />
  </CollapsibleSection>
));
const MaterialSection = memo<{
  config: SceneConfig;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
}>(({ config, set }) => (
  <CollapsibleSection icon={<Layers className="w-3.5 h-3.5" />} title="鏉愯川灞炴€

绾挎妯″紡" onChange={(v) => set('wireframe', v)} />
  </CollapsibleSection>
));
const RenderStyleSection = memo<{
  config: SceneConfig;
  expandedSections: Record<string, boolean>;
  set: <K extends keyof SceneConfig>(k: K, v: SceneConfig[K]) => void;
  toggleSection: (key: string) => void;
}>(({ config, set, expandedSections, toggleSection }) => (
  <CollapsibleSection
    expanded={expandedSections.style}
    icon={<Palette className="w-3.5 h-3.5" />}
    onToggle={() => toggleSection('style')}
    title="娓叉煋椋庢牸"
  >
    <div className="grid grid-cols-3 gap-1.5">
      {RENDER_STYLES.map((r) => (
        <CardBtn
          active={config.renderStyle === r.style}
          key={r.style}
          onClick={() => set('renderStyle', r.style)}
        >
          {typeof r.icon === 'string' ? (
            <span className="text-sm mb-0.5">{r.icon}</span>
          ) : (
            <span className="mb-0.5">{r.icon}</span>
          )}
          <span>{r.label}</span>
        </CardBtn>
      ))}
    </div>

    {config.renderStyle === RenderStyle.HOLOGRAPHIC ? (
      <div className="mt-3 p-2 rounded-lg bg-cyan-500/5 border border-cyan-500/20 space-y-2">
        <div className="text-[10px] text-cyan-400 font-medium mb-2">鍏ㄦ伅鍙傛暟</div>
        <div className="grid grid-cols-4 gap-1">
          {HOLOGRAM_TYPES.map((h) => (
            <Btn
              active={config.hologramType === h.type}
              key={h.type}
              onClick={() => set('hologramType', h.type)}
              small
            >
              {h.label}
            </Btn>
          ))}
        </div>
        <Slider
          label="鎵弿绾縗

鍣偣寮哄害"
          max={HOLOGRAM.GLITCH_MAX}
          min={HOLOGRAM.GLITCH_MIN}
          onChange={(v) => set('hologramGlitch', v)}
          step={HOLOGRAM.GLITCH_STEP}
          value={config.hologramGlitch}
        />
        <Toggle
          checked={config.hologramUseOriginalColor}
          label="淇濈暀鍘熻壊"
          onChange={(v) => set('hologramUseOriginalColor', v)}
        />
        <Slider
          label="涓嶉€忔槑搴

鏌撹壊寮哄害"
          max={HOLOGRAM.TINT_MAX}
          min={HOLOGRAM.TINT_MIN}
          onChange={(v) => set('hologramTint', v)}
          step={HOLOGRAM.TINT_STEP}
          value={config.hologramTint}
        />
      </div>
    ) : null}

    {config.renderStyle === RenderStyle.SKETCH ? (
      <div className="mt-3 p-2 rounded-lg bg-zinc-500/5 border border-zinc-500/20 space-y-2">
        <div className="text-[10px] text-zinc-300 font-medium mb-2">绱犳弿鍙傛暟</div>
        <Slider
          label="绗旇Е瀹藉害"
          max={SKETCH_PARAMS.STROKE_WIDTH_MAX}
          min={SKETCH_PARAMS.STROKE_WIDTH_MIN}
          onChange={(v) => set('sketchStrokeWidth', v)}
          step={SKETCH_PARAMS.STROKE_WIDTH_STEP}
          value={config.sketchStrokeWidth ?? 1.5}
        />
        <Slider
          label="杈圭紭闃堝€糪

绾稿紶棰楃矑"
          max={SKETCH_PARAMS.PAPER_GRAIN_MAX}
          min={SKETCH_PARAMS.PAPER_GRAIN_MIN}
          onChange={(v) => set('sketchPaperGrain', v)}
          step={SKETCH_PARAMS.PAPER_GRAIN_STEP}
          value={config.sketchPaperGrain ?? 0.6}
        />
        <Slider
          label="鎺掔嚎瀵嗗害"
          max={SKETCH_PARAMS.HATCH_DENSITY_MAX}
          min={SKETCH_PARAMS.HATCH_DENSITY_MIN}
          onChange={(v) => set('sketchHatchDensity', v)}
          step={SKETCH_PARAMS.HATCH_DENSITY_STEP}
          value={config.sketchHatchDensity ?? 0.5}
        />
      </div>
    ) : null}
  </CollapsibleSection>
));

export type { EffectsTabProps };

RenderStyleSection.displayName = 'RenderStyleSection';
ColorGradeSection.displayName = 'ColorGradeSection';
LightingSection.displayName = 'LightingSection';
EffectsToggleSection.displayName = 'EffectsToggleSection';
MaterialSection.displayName = 'MaterialSection';
EffectsTab.displayName = 'EffectsTab';

import { Preload } from '@react-three/drei';
import { Canvas } from '@react-three/fiber';
import { forwardRef, memo, useImperativeHandle, useRef } from 'react';

import {
  calculateDistance,
  calculatePresetPoseForProjection,
  CoreControllerProvider,
} from '@/features/scene/services/camera';
import { PerformanceOverlay } from '@/shared/components';
import { SCENE_CONFIG } from '@/shared/constants';
import { RenderStyle } from '@/shared/types';
import { useSceneStore } from '@/stores/sharedStore';

import { CameraRig, SceneExporter, SceneRecorder } from './components';
import {
  CoordinateDebug,
  InputBindingEffect,
  ToneMappingEffect,
  TrackingBridge,
  WebcamTracker,
} from './components/effects';
import { SceneContent } from './components/SceneContent';
import { useColorGrade, useVideoControl } from './hooks';

import type { ExporterRef, RecordingRef } from './components';
import type { CameraPresetType } from '@/features/scene/services/camera';
import type { CameraViewPreset } from '@/shared/types';
import type { Group, VideoTexture } from 'three';
import type { OrbitControls as OrbitControlsType } from 'three-stdlib';

export interface SceneViewerHandle {
  captureVideoFrame: () => void;
  downloadSnapshot: () => void;
  exportScene: () => void;
  seekVideo: (time: number) => void;
  setCameraView: (view: CameraViewPreset) => void;
  startRecording: (withAudio?: boolean) => void;
  stopRecording: () => void;
}
interface SceneViewerProps {
  aspectRatio: number;
  backgroundUrl: string | null;
  depthUrl: string | null;
  imageUrl: string | null;
  isVideoPlaying: boolean;
  onVideoDurationChange?: (duration: number) => void;
  onVideoEnded?: () => void;
  onVideoTimeUpdate?: (time: number) => void;
  videoUrl: string | null;
}

export const SceneViewer = memo(
  forwardRef<SceneViewerHandle, SceneViewerProps>((props, ref) => {
    const {
      imageUrl,
      depthUrl,
      backgroundUrl,
      videoUrl,
      aspectRatio,
      isVideoPlaying,
      onVideoTimeUpdate,
      onVideoDurationChange,
      onVideoEnded,
    } = props;
    const renderStyle = useSceneStore((state) => state.config.renderStyle);
    const hologramType = useSceneStore((state) => state.config.hologramType);
    const enableVignette = useSceneStore((state) => state.config.enableVignette);
    const exposure = useSceneStore((state) => state.config.exposure);
    const config = useSceneStore((state) => state.config);
    const exporterRef = useRef<ExporterRef>(null);
    const recorderRef = useRef<RecordingRef>(null);
    const controlsRef = useRef<OrbitControlsType | null>(null);
    const videoTextureRef = useRef<VideoTexture | null>(null);
    const sceneGroupRef = useRef<Group>(null);
    const colorGradeStyle = useColorGrade();
    const { seek } = useVideoControl({
      videoTextureRef,
      isPlaying: isVideoPlaying,
      onTimeUpdate: onVideoTimeUpdate,
      onDurationChange: onVideoDurationChange,
      onEnded: onVideoEnded,
    });

    useImperativeHandle(ref, () => ({
      exportScene: () => exporterRef.current?.exportScene(),
      downloadSnapshot: () => exporterRef.current?.downloadSnapshot(),
      captureVideoFrame: () => recorderRef.current?.captureVideoFrame(),
      startRecording: (withAudio) => recorderRef.current?.startRecording(withAudio),
      stopRecording: () => recorderRef.current?.stopRecording(),
      seekVideo: seek,
      setCameraView: (view) => {
        const controls = controlsRef.current;

        if (!controls) return;
        const camera = controls.object;
        const currentDist = calculateDistance(
          { x: camera.position.x, y: camera.position.y, z: camera.position.z },
          { x: controls.target.x, y: controls.target.y, z: controls.target.z }
        );
        const presetPose = calculatePresetPoseForProjection(
          view as CameraPresetType,
          config.projectionMode,
          config.isImmersive,
          currentDist
        );

        camera.position.set(presetPose.position.x, presetPose.position.y, presetPose.position.z);
        controls.target.set(presetPose.target.x, presetPose.target.y, presetPose.target.z);
        controls.update();
      },
    }));
    if (!imageUrl || !depthUrl) return null;
    const planeWidth = SCENE_CONFIG.PLANE_BASE_WIDTH;
    const planeHeight = planeWidth / aspectRatio;

    return (
      <div
        className="w-full h-full bg-black relative rounded-lg overflow-hidden shadow-2xl border border-zinc-800 transition-all duration-200"
        style={colorGradeStyle}
      >
        <PerformanceOverlay position="bottom-left" visible />

        {enableVignette ? (
          <div
            className="absolute inset-0 z-20 pointer-events-none mix-blend-multiply"
            style={{
              backgroundImage: `radial-gradient(circle at center, transparent 55%, rgba(0,0,0,${Math.max(0, Math.min(1, 0.2 + config.vignetteStrength * 0.8))}) 100%)`,
            }}
          />
        ) : null}

        {renderStyle === RenderStyle.HOLOGRAPHIC && (
          <div className="absolute top-4 right-4 z-10 text-cyan-400 text-xs font-mono animate-pulse border border-cyan-500/50 px-2 py-1 rounded bg-cyan-900/20 shadow-[0_0_10px_rgba(34,211,238,0.3)]">
            HOLOGRAM ACTIVE 路 {hologramType}
          </div>
        )}

        <CoreControllerProvider autoInit>
          <Canvas dpr={[1, 2]} gl={{ preserveDrawingBuffer: true }} shadows>
            <ToneMappingEffect exposure={exposure} />
            <InputBindingEffect />
            <CoordinateDebug
              depthUrl={depthUrl}
              planeHeight={planeHeight}
              planeWidth={planeWidth}
              sceneGroupRef={sceneGroupRef}
            />
            <TrackingBridge
              depthUrl={depthUrl}
              planeHeight={planeHeight}
              planeWidth={planeWidth}
              sceneGroupRef={sceneGroupRef}
            />

            <WebcamTracker />
            <CameraRig config={config} controlsRef={controlsRef}>
              <SceneContent
                aspectRatio={aspectRatio}
                backgroundUrl={backgroundUrl}
                depthUrl={depthUrl}
                imageUrl={imageUrl}
                sceneGroupRef={sceneGroupRef}
                videoTextureRef={videoTextureRef}
                videoUrl={videoUrl}
              />
            </CameraRig>
            <SceneRecorder ref={recorderRef} videoTexture={videoTextureRef.current} />
            <SceneExporter ref={exporterRef} sceneGroupRef={sceneGroupRef} />
            <Preload all />
          </Canvas>
        </CoreControllerProvider>
      </div>
    );
  })
);

SceneViewer.displayName = 'SceneViewer';

export const AXES_HELPER_SIZE = 5;
export const BACKGROUND_SPHERE_SCALE = [100, 100, 100] as const;
export const BACKGROUND_SPHERE_SEGMENTS = 32;
export const DIRECTIONAL_LIGHT_INTENSITY_MULTIPLIER = 0.5;
export const DIRECTIONAL_LIGHT_POSITION = [10, 10, 5] as const;
export const GRID_COLOR_CENTER = 0x444444;
export const GRID_COLOR_GRID = 0x222222;
export const GRID_DIVISIONS = 20;
export const GRID_SIZE = 20;
export const SCENE_BASE_WIDTH = 10;
export const WRAPPED_PROJECTION_ANGLE_THRESHOLD = 355;

export const BOUNDING_EXPANSION_MULTIPLIER = 2;
export const BOX_DENSITY = {
  CUBE_DIVISOR: 2,
  INFINITE_BOX_DIVISOR: 4,
} as const;
export const BOX_FACE = {
  VERTICES_PER_FACE: 4,
  FRONT: 0,
  BACK: 1,
  TOP: 2,
  BOTTOM: 3,
  RIGHT: 4,
  LEFT: 5,
} as const;
export const CORNER_POSITION_OFFSET = 0.4;
export const CYLINDER_RADIUS_FACTOR = 0.9;
export const DEG_TO_RAD = Math.PI / 180;
export const DOME_PHI_LENGTH = Math.PI / 2;
export const INFINITE_BOX_SCALE = 2;
export const MAX_CORNER_PLANE_RES = 128;
export const MAX_SPLAT_DENSITY = 384;
export const SPHERE_RADIUS = {
  STANDARD: 0.8,
  PANORAMA: 0.6,
  DOME: 0.8,
} as const;
export const UV_BOUNDS = {
  QUARTER: 0.25,
  THREE_QUARTER: 0.75,
  ZERO: 0.0,
  ONE: 1.0,
} as const;

import * as faceDetection from '@tensorflow-models/face-detection';
import * as tf from '@tensorflow/tfjs';
import { useEffect, useRef, useState } from 'react';

import { getEventBus } from '@/core/EventBus';
import { TrackingEvents } from '@/core/EventTypes';
import { createLogger } from '@/core/Logger';
import { FONT_SIZE, OPACITY, RADII, SPACING, Z_INDEX } from '@/shared/constants/ui';

import { COORDINATE_TRANSFORM, WEBCAM } from './constants';

const logger = createLogger({ module: 'WebcamTracker' });
const useFaceModel = () => {
  const [model, setModel] = useState<faceDetection.FaceDetector | null>(null);

  useEffect(() => {
    const initModel = async () => {
      try {
        await tf.ready();
        const modelType = faceDetection.SupportedModels.MediaPipeFaceDetector;
        const detectorConfig: faceDetection.MediaPipeFaceDetectorMediaPipeModelConfig = {
          runtime: 'mediapipe',
          solutionPath: 'https://cdn.jsdelivr.net/npm/@mediapipe/face_detection',
        };

        const detector = await faceDetection.createDetector(modelType, detectorConfig);

        setModel(detector);
        logger.info('Face detection model loaded');
      } catch (error) {
        logger.error('Failed to load face detection model', { error });
      }
    };

    void initModel();
  }, []);

  return model;
};
const useWebcamStream = (videoRef: React.RefObject<HTMLVideoElement | null>) => {
  const [isTracking, setIsTracking] = useState(false);

  useEffect(() => {
    const startWebcam = async () => {
      if (videoRef.current) {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({
            video: {
              width: WEBCAM.DEFAULT_RESOLUTION.WIDTH,
              height: WEBCAM.DEFAULT_RESOLUTION.HEIGHT,
              facingMode: WEBCAM.FACING_MODE,
            },
          });

          videoRef.current.srcObject = stream;
          videoRef.current.onloadedmetadata = () => {
            void videoRef.current?.play();
            setIsTracking(true);
          };
        } catch (error) {
          logger.error('Failed to access webcam', { error });
        }
      }
    };

    void startWebcam();

    return () => {
      if (videoRef.current?.srcObject) {
        const stream = videoRef.current.srcObject as MediaStream;

        stream.getTracks().forEach((track) => track.stop());
      }
    };
  }, [videoRef]);

  return isTracking;
};

export const WebcamTracker = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const model = useFaceModel();
  const isTracking = useWebcamStream(videoRef);

  useEffect(() => {
    let animationFrameId: number;

    const trackFaces = async () => {
      if (model && videoRef.current && isTracking && videoRef.current.readyState === 4) {
        try {
          const faces = await model.estimateFaces(videoRef.current, { flipHorizontal: false });

          if (faces.length > 0) {
            const face = faces[0];

            if (!face) return;

            const { box } = face;

            const { videoWidth, videoHeight } = videoRef.current;

            if (!videoWidth || !videoHeight) return;

            const centerX = box.xMin + box.width / 2;
            const centerY = box.yMin + box.height / 2;

            const x =
              (centerX / videoWidth) * COORDINATE_TRANSFORM.NORMALIZATION_FACTOR -
              COORDINATE_TRANSFORM.NORMALIZATION_OFFSET;
            const y =
              -(centerY / videoHeight) * COORDINATE_TRANSFORM.NORMALIZATION_FACTOR +
              COORDINATE_TRANSFORM.NORMALIZATION_OFFSET;

            const widthRatio = box.width / videoWidth;
            const z = (1 - widthRatio) * COORDINATE_TRANSFORM.DEPTH_SCALE_FACTOR;

            getEventBus().emit(TrackingEvents.POINT_3D, {
              x,
              y,
              z,
              confidence: 1,
              timestamp: Date.now(),
            });
          }
        } catch (_err) {
          // ignore
        }
      }
      animationFrameId = requestAnimationFrame(() => void trackFaces());
    };

    if (isTracking && model) {
      void trackFaces();
    }

    return () => cancelAnimationFrame(animationFrameId);
  }, [model, isTracking]);

  return (
    <div
      style={{
        position: 'absolute',
        top: SPACING.LG,
